
function ReadDateTimeShow(){var year=new Date().getFullYear();var Month=new Date().getMonth()+1;var Day=new Date().getDate();var Time=new Date().toLocaleTimeString();var nowDate=year+"年"+Month+"月"+Day+"日 "+Time;$("#nowDate").text(nowDate);}
function BindMenuData(){$("#acMenu").accordion({animate:false});$.getJSON("/Test/LoadMenuData",{},function(data){for(var i=0;i<data.length;i++){var groupInfo=data[i];var strMenuItemHTMl="<ul>";for(var j=0;j<groupInfo.menuC.length;j++){var menuItem=groupInfo.menuC[j];strMenuItemHTMl+='<li><a href="javascript:void(0)" src="'+menuItem.url
+'" class="menuLink">'+menuItem.title+'</a></li>';}
strMenuItemHTMl+="</ul>";$("#acMenu").accordion('add',{title:groupInfo.menuP,content:strMenuItemHTMl});$("#acMenu").accordion('select',0);}
BindMenuClickHrefEvent();});}
function BindMenuClickHrefEvent(){$(".menuLink").click(function(){var src=$(this).attr("src");var tabTitle=$(this).text();var strHtml='<iframe id="frmContent" width="100%" height="100%" frameborder="0"'
+' scrolling="auto" src="'+src+'"></iframe>';if($('#tabCenter').tabs('exists',tabTitle)){$('#tabCenter').tabs('select',tabTitle);}else{$('#tabCenter').tabs('add',{title:tabTitle,content:strHtml,closable:true});}});}
function closeTab(action){var onlyOpenTitle="办公桌面";var alltabs=$('#tabCenter').tabs('tabs');var currentTab=$('#tabCenter').tabs('getSelected');var allTabtitle=[];$.each(alltabs,function(i,n){allTabtitle.push($(n).panel('options').title);})
switch(action){case"refresh":var iframe=$(currentTab.panel('options').content);var src=iframe.attr('src');$('#tabCenter').tabs('update',{tab:currentTab,options:{content:createFrame(src)}})
break;case"close":var currtab_title=currentTab.panel('options').title;if(currtab_title!=onlyOpenTitle)
$('#tabCenter').tabs('close',currtab_title);break;case"closeall":$.each(allTabtitle,function(i,n){if(n!=onlyOpenTitle){$('#tabCenter').tabs('close',n);}});break;case"closeother":var currtab_title=currentTab.panel('options').title;$.each(allTabtitle,function(i,n){if(n!=currtab_title&&n!=onlyOpenTitle){$('#tabCenter').tabs('close',n);}});break;case"closeright":var tabIndex=$('#tabCenter').tabs('getTabIndex',currentTab);if(tabIndex==alltabs.length-1){return false;}
$.each(allTabtitle,function(i,n){if(i>tabIndex){if(n!=onlyOpenTitle){$('#tabCenter').tabs('close',n);}}});break;case"closeleft":var tabIndex=$('#tabCenter').tabs('getTabIndex',currentTab);if(tabIndex==1){return false;}
$.each(allTabtitle,function(i,n){if(i<tabIndex){if(n!=onlyOpenTitle){$('#tabCenter').tabs('close',n);}}});break;case"exit":$('#closeMenu').menu('hide');break;}}
function tabCloseEven(){$("#tabCenter").tabs({onContextMenu:function(e,title){e.preventDefault();$('#tabsMenu').menu('show',{left:e.pageX,top:e.pageY}).data("tabTitle",title);}});$('#tabsMenu').menu({onClick:function(item){closeTab(item.id);}});return false;}
function createFrame(url){var iframe='<iframe scrolling="auto" frameborder="0" src="'+url
+'" width="100%" height="100%"></iframe>';return iframe;}